# ورود به حساب تلگرامی
client = TelegramClient('session_name', api_id, api_hash)

# تعریف الگوی رگولار برای ریپلی به پیامی که `/Amirzxr` را ریپلی کرده است
@client.on(events.NewMessage(pattern=r'تارگت'))
async def add_target(event):
    # دریافت اطلاعات پیامی که روی آن ریپلی شده است
    replied_message = await event.get_reply_message()
    if replied_message:
        # افزودن فرد مورد نظر به لیست تارگت‌ها
        targets.append(replied_message.sender_id)
        await event.reply("شخص مورد نظر به لیست تارگت‌ها افزوده شد.")

# تعریف الگوی رگولار برای ریپلی به هر پیامی که در گروه ارسال می‌شود
@client.on(events.NewMessage())
async def reply_to_target(event):
    if event.sender_id in targets:
        random_reply = random.choice(reply_texts)
        await event.reply(random_reply)

# ورود به حساب تلگرامی
client.start()
client.run_until_disconnected()